import {all, fork, takeEvery, put,delay } from 'redux-saga/effects';
import {LOG_IN_REQUEST,LOG_IN_SUCCESS,LOG_IN_FAILURE} from '../reducers/user';
import {SIGN_UP_REQUEST,SIGN_UP_SUCCESS,SIGN_UP_FAILURE} from '../reducers/user';
import axios from 'axios';


function* loginAPI(){ //서버에 요청을 보내는 부분
    return axios.post('/login');
}
function* login() {
    try {
      // yield call(loginAPI);
        yield delay(2000);
        yield put({ // put은 dispatch 동일
        type: LOG_IN_SUCCESS,
        });
    } catch (e) { // loginAPI 실패
        console.error(e);
        yield put({
            type: LOG_IN_FAILURE,
        });
    }
}
function* watchLogin(){
    yield takeEvery(LOG_IN_REQUEST,login)
}



function* signupAPI(signUpData){ //서버에 요청을 보내는 부분
    return axios.post('/http://localhost:3065/api/user/');
}
function* signUp(action){//와치했을때 무엇을 할지 정하는 함수
    try{
        yield call(signupAPI,action.data);
        yield put({ //reducer의 dispatch와 동일
            type:SIGN_UP_SUCCESS,
        });
    }catch(e){ //loginAPI 실패시
        yield put({
            type:SIGN_UP_FAILURE,
            error:e,
    });
}
}
function* watchSignUp(){ //등록한 이름을 와치하는 함수
    yield takeEvery(SIGN_UP_REQUEST,signUp);
}


export default function* userSaga() {
    yield all([
      fork(watchLogin),
      fork(watchSignUp),
    ]);
  }
