const dummyUser = {
	nickname: 'kosun',
	friend:[],
	follwing:[],
	follower:[],
	signUpData:{},
};

export const initialState={
	isLoggedIn: false,
	user: null,
};

export const SIGN_UP='SIGN_UP';
export const LOG_IN = 'LOG_IN'; //액션이름
export const LOG_OUT = 'LOG_OUT';

export const signUpAction = (data) => {
	return{
		type:SIGN_UP,
		data:data,
	};
};
export const loginAction = {
	type:LOG_IN,
};
export const logoutAction = {
	type:LOG_OUT,
};

export default (state = initialState,action) => {
	switch (action.type){
		case LOG_IN : {
			return{
				...state,
				isLoggedIn:true,
				user:dummyUser,
			};
		}
		case LOG_OUT : {
			return {
				...state,
				isLoggedIn:false,
				user:{},
			};
		}
		case SIGN_UP:{
			return{
				...state,
				signUpData: action.data,
			};
		}
		default:{
			return{
				...state,
			};
		}
	};
};
