import React, {useState,useCallback} from 'react';
import {Input, Button, Form} from 'antd';
import {useInput} from '../pages/signup';
import Link from 'next/link';
import {useDispatch} from 'react-redux';
import { loginAction } from '../reducers/user';

const LogInForm = () => {
    const [id,onChangeId] = useInput('');
    const [password,onChangePassword] = useInput('');
    const dispatch = useDispatch();

    
    const onSubmitForm = useCallback((e) => {
        e.preventDefault();
        dispatch(loginAction);
    },[id,password]);

    return (
        <>
        <Form onSubmit={onSubmitForm} style={{padding:'10px'}} > 
        <div>
            <label htmlFor="user-id">id</label>
            <br/>
            <input name="user-id" required onChange={onChangeId}/>
        </div>
        <div>
            <label htmlFor="user-password">password</label>
            <br/>
            <input name="user-password" type="password" required value={password} onChange={onChangePassword} />
        </div>
        <div style={{marginTop: '10px'}} >
            <Button type="primary" htmlType="submit" loading={false}>Log In</Button>
            <Link href="/signup">
                <a><Button>Sign Up</Button></a>
            </Link>
        </div> 
        </Form>
    </>
    )
}
export default LogInForm;
