import React, { Component } from 'react';
import {Menu,Input,Row,Col,Card,Avatar} from 'antd';
import Link from 'next/link';
import useModal from '../node_modules/antd/lib/modal/useModal';
import { useForm } from '../node_modules/rc-field-form/lib';
import LogInForm from './LogInForm';
import UserProfile from './UserProfile';
import {useSelector} from 'react-redux';


const AppLayout = ({children}) => {
	const {isLoggedin} = useSelector(state=>state.user);
		return (
			<>
			<div>
			<Menu mode="horizontal">
					<Menu.Item key="home"><Link href="/"><a>NodeBird</a></Link></Menu.Item>
					<Menu.Item key="profile"><Link href="/profile"><a>My Profile</a></Link></Menu.Item>
					<Menu.Item key="mail">
						<Input.Search enterButton style={{ verticalAlign:'middle' }}/>
					</Menu.Item>
			</Menu>
			
			<Row gutter={10}>
					<Col xs={24} md={6} >
						{isLoggedin 
						? 
						<UserProfile />
						: 
						<LogInForm /> }
					</Col>

					<Col xs={24} md={12} >
						{children}
					</Col>
					<Col xs={24} md={6} >
						<Link href="http://www.naver.com"><a target="_blank">Made by Molly Kim</a></Link>
					</Col>
			</Row>

			</div>
			</>
		);
	
}

export default AppLayout;
